package Lec60;

public class Right_Shift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 13;
		System.out.println(n >> 1);
		int n1 = -13;
		System.out.println(n1 >> 1);
		int a = -22;
		System.out.println(a >> 2);
		n = -1;
		n = n >> 1;
		System.out.println(n);
	}

}
